let movieInfo = 'not working'
let movieData = 'empty'

function getTMDBdata(){
    fetch('https://api.themoviedb.org/3/movie/now_playing?api_key=15e1bbc41548aa6ea02bac4f092efa6f&language=en-US&page=1')
    .then((response) => response.json())
    .then(function(data){
        movieInfo = data.results[0]
    })
    .catch(function(error){
        movieInfo = error
    })  
    return movieInfo
}

