import React from 'react';
import axios from 'axios';


const apiURL = "https://api.themoviedb.org/3/movie/now_playing?api_key=15e1bbc41548aa6ea02bac4f092efa6f&language=en-US&page=1"

// const axios = require('axios');


var info = []

function getTMDBdata(){
    axios.get('https://api.themoviedb.org/3/movie/now_playing?api_key=15e1bbc41548aa6ea02bac4f092efa6f&language=en-US&page=1')
    .then(response => JSON.stringify(response))
    .then(response => {
        info = response.toString().split(',')
    })
    .catch(function(error){
        alert(error)
    })  
    return info
}


function getTitleCount(str){
    var str2 = str.toString()
    var count = 0
    var list2 = str2.split('"')
    var list3 = []
    for(let i = 0; i < list2.length; i ++){
        if(list2[i] == "title"){
            count +=1
            list2[i].replace('title', '')
            list3.push(list2[i])
        }
    }
    return list3
}



function getTitles(str){
    var list = []
    var str2 = str.toString()
    for(let i=0; i<str2.length; i++){
        if(str2.substr(i, i + 4) == "title"){
        // if(str2.charAt(i) == "o" && str2.charAt(i + 1) == 'r' && str2.charAt(i+13) == "e"){
              list.push(str2.slice(i + 17, i + 36)) 
        }

    }
    return list
}


function getNiceListofTitles(list){
    var newlist = ''
    // for(let i = 0; i < list.length; i++){
    //  newlist += list[i] + "/n"
    // }
    return list.map((item)=>{
      return <p>{item}</p>
    })
}



function NowPlaying() {

    return ( 
        <div>
        <h1 className='page-name'>Now Playing</h1>  
        <span>{`There are ${getTitleCount(getTMDBdata())} movies currently playing in theatres:`}</span>
        <br></br>
        <br></br>
        <span>{getNiceListofTitles(getTitles(getTMDBdata()))}</span>
        </div>
    )
    }
export default NowPlaying
